import React, { Component } from 'react'
import '../App.css';

class Login extends Component {
    constructor() {
        super()
        this.state = {
            email: '',
            password: '',
            errors: []
        }
        this.onSubmit = this.onSubmit.bind(this)
    }

    onChange(e) {
        this.setState({ [e.target.name]: e.target.value })
    }
    onSubmit(e) {
        e.preventDefault()
        const errors = validate(this.state.email, this.state.password)
        if (errors.length > 0) {
            this.setState({ errors });
        }
        else
        {
            window.alert('Login successful')
        }

    }
    render() {
        const { errors } = this.state;
        return (
<div class="login">
    <form noValidate>
        <h2 class="text-center">Log in</h2>       
        <div class="form-group">
            <input type="email" class="form-control" placeholder="Email"  value={this.state.email}/>
        </div>
        <div class="form-group">
            <input type="password" class="form-control" placeholder="Password" value={this.state.password}/>
        </div>
        <div class="form-group">
            <button type="submit" class="btn btn-dark btn-block" onClick={this.onSubmit}>Log in</button>
        </div>
        <div class="clearfix">
            <label class="pull-left checkbox-inline"><input type="checkbox"/> Remember me</label>
            <a href="/forgotPassword" class="pull-right">Forgot Password?</a>
        </div>        
    </form>
    <p class="text-center"><a href="/notbuilt">Create an Account</a></p>
    {errors.map(error => (
                 <p key={error} className='error'>Error on Page: {error}</p>
                            ))}
</div>
  )
    }


}

export default Login

const validate = (email, password) => {
    const errors = [];
    const validEmailRegex =
        RegExp(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/);
    if (!validEmailRegex.test(email)) {
        errors.push("Email id is not valid")
    }
    if (password.length < 6) {
        errors.push("Password should be atleast 6 characters long");
    }


    return errors;
}

