import React, { Component } from 'react'
import {Form,Button, FormLabel} from 'react-bootstrap';
import '../App.css';

class ForgotPassword extends Component {
    constructor() {
        super()
        this.state = {
            email: '',
            password: '',
            errors: []
        }
        this.onChange = this.onChange.bind(this)
        this.onSubmit = this.onSubmit.bind(this)
    }
    onChange(e) {
        this.setState({ [e.target.name]: e.target.value })
    }
    onSubmit(e) {
        e.preventDefault()
        const errors = validate(this.state.email, this.state.password)
        if (errors.length > 0) {
            this.setState({ errors });
            setTimeout(()=>{ this.setState({errors:[]}) }, 3000);
        }
        else
        {
            window.alert('Login successful')
            this.errors=[];
            this.setState({ errors });
        }     
    }
    render() {
        const { errors } = this.state;
        return (
<div class="login-form">
    <form>
        <h2 class="text-center">Reset Password</h2>       
        <div class="form-group">
            <input type="email" class="form-control" placeholder="Email" required="required"/>
        </div>
        <div class="form-group">
        <div class="row rowCenter">
        <div clas="col">
            <button bsSize="small" type ="submit" class="btn btn-dark btn-block">Submit</button>
            </div>
            <span className="px-1"/>
            <div clas="col">
            <button bsSize="small"  type="button" class="btn btn-dark btn-block" onClick={this.handleClick}>cancel</button>
            </div>
            </div>
        </div> 
    </form>
</div>
  )
    }
}
const validate = (email) => {
    const errors = [];
    const validEmailRegex =
        RegExp(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/);
    if (!validEmailRegex.test(email)) {
        errors.push("Email id is not valid")
    }


    return errors;
}

export default ForgotPassword
