import '../App.css';

class Login extends Component {
    constructor() {
        super()
        this.state = {
            name: '',
            password: ''

        }
        this.onSubmit = this.onSubmit.bind(this)
    }

    onSubmit(e) {
        e.preventDefault()
        if(this.state.name!=''&& this.state.password!='')
        {
            window.alert('Login successful')
        }

    }
    render() {
        return (
<div class="login">
    <form>
        <h2 class="text-center">Log in</h2>       
        <div class="form-group">
            <input type="text"  name="name" class="form-control" placeholder="Username"  value={this.state.name}  required="required"/>
        </div>
        <div class="form-group">
            <input type="password"  name="password" class="form-control" placeholder="Password"value={this.state.password} required="required"/>
        </div>
        <div class="form-group">
            <button type="submit" class="btn btn-dark btn-block" onClick="validateForm()">Log in</button>
        </div>
        <div class="clearfix">
            <label class="pull-left checkbox-inline"><input type="checkbox"/> Remember me</label>
            <a href="/forgotPassword" class="pull-right">Forgot Password?</a>
        </div>        
    </form>
    <p class="text-center"><a href="/notbuilt">Create an Account</a></p>
</div>
  )
    }


}

export default Login

